switch(msg.payload) {
    case "start_timer":
        if(!$scope.myTimer) {
            $scope.myTimer = setInterval(function() {
                var randomColor = (Math.random()*100.0)%30;
                $("#textAtSvg").attr("x", randomColor);
            }, 100);
        }
        break;
    case "stop_timer":
        if($scope.myTimer) {
            clearInterval($scope.myTimer);
            $scope.myTimer = null;
        }
        break;
}

