function rotByZ( xyz,deg){
    x = xyz[0];
    y = xyz[1];
    z = xyz[2];

	xr = x*Math.cos(deg) - y*Math.sin(deg);
	yr = x*Math.sin(deg) + y*Math.cos(deg);
	zr = z;

	return [xr,yr,zr];
}

function rotByX( xyz,deg){
    x = xyz[0];
    y = xyz[1];
    z = xyz[2];


	yr = y*Math.cos(deg) - z*Math.sin(deg);
	zr = y*Math.sin(deg) + z*Math.cos(deg);
	xr = x;

	return [xr,yr,zr];
}

function rotByY( xyz,deg){
    x = xyz[0];
    y = xyz[1];
    z = xyz[2];


	zr = z*Math.cos(deg) - x*Math.sin(deg);
	xr = z*Math.sin(deg) + x*Math.cos(deg);
	yr = y;

	return [xr,yr,zr];
}

