const mutil = (function () {
    'use strict';
    var cc = 0;

    function add(a, b) {
        return a + b;
    }

    function getHello( ){
        return "hello "+cc++;
    }

    function degPos( deg ){
        deg = deg%360;
        if( deg < 0 )
            deg+=360;
        return deg;
    }

    function degDelta( deg0, deg1 ){
        deg0 = degPos(deg1)-degPos(deg0);

        if( deg0 > 180 )
            deg0-=360;
        if( deg0 < -180 )
            deg0+=360;

        return deg0;
    }

    function mMapVal( val, minI, maxI, minO, maxO, inLimits = false){
      var dI = maxI - minI;
      var dO = maxO - minO;
      var nI = ( val - minI )/dI;

      if( inLimits ){
        var tr = minO + ( nI*dO );
        if( tr < minO )
          return minO;
        else if( tr > maxO )
          return maxO;
        else
          return tr;

      }else
        return minO + ( nI*dO );

    }



    class Controller {
      helloCount = 0;
      updatesCount = 0;

      constructor(k_p, k_i, k_d, dt) {
        let i_max;
        if (typeof k_p === 'object') {
          let options = k_p;
          k_p = options.k_p;
          k_i = options.k_i;
          k_d = options.k_d;
          dt = options.dt;
          i_max = options.i_max;
        }

        // PID constants
        this.k_p = (typeof k_p === 'number') ? k_p : 1;
        this.k_i = k_i || 0;
        this.k_d = k_d || 0;

        // Interval of time between two updates
        // If not set, it will be automatically calculated
        this.dt = dt || 0;

        // Maximum absolute value of sumError
        this.i_max = i_max || 0;

        this.sumError  = 0;
        this.lastError = 0;
        this.lastTime  = 0;

        this.target    = 0; // default value, can be modified with .setTarget
      }

      getHello(){
          return "hello from Controller"+this.helloCount++;
      }

      setTarget(target) {
        this.target = target;
      }

      update(currentValue) {
        if(!currentValue) throw new Error("Invalid argument");
        this.currentValue = currentValue;
        this.updatesCount++;

        // Calculate dt
        let dt = this.dt;
        if (!dt) {
          let currentTime = Date.now();
          if (this.lastTime === 0) { // First time update() is called
            dt = 0;
          } else {
            dt = (currentTime - this.lastTime) / 1000; // in seconds
          }
          this.lastTime = currentTime;
        }
        if (typeof dt !== 'number' || dt === 0) {
          dt = 1;
        }

        let error = (this.target - this.currentValue);
        this.sumError = this.sumError + error*dt;
        if (this.i_max > 0 && Math.abs(this.sumError) > this.i_max) {
          let sumSign = (this.sumError > 0) ? 1 : -1;
          this.sumError = sumSign * this.i_max;
        }

        let dError = (error - this.lastError)/dt;
        this.lastError = error;

        // differential_error - delta
        // heading_error = heading_to_steer - heading; 
        // differential_error = bearingrate; // 0 - 0.5
        //PID_output = PID_Ks[0] * (PID_Ks[1] * heading_error  - PID_Ks[2] * differential_error); 
        //rudder_command = rudder_command + PID_output;


        return (this.k_p*error) + (this.k_i * this.sumError) + (this.k_d * dError);
      }

      reset() {
        this.sumError  = 0;
        this.lastError = 0;
        this.lastTime  = 0;
      }
    }


    function getPid(){
        return pid;
    }   

    function toDegrees ( rad ) {
      return rad * (180 / Math.PI);
    }
    function getAngle( x0, y0, x1, y1 ){
      var dx = x1-x0;
      var dy = y1-y0;
      return toDegrees( Math.atan2( dx, dy ) );
    }


    var sec1 = 1000;
    var sec30 = sec1*30;
    var min1 = 2*sec30;
    var min5 = min1*5;

    var storeData = {};
    function storeIt( key, val, forMs ){
      var t = new Date().getTime();
      //new Date().getTime() / 1000
      //console.log("t:"+t);
      // todo
      if( storeData[key] == undefined )
        storeData[key] = new Array();

        storeData[key].push({
          'v': val,
          't': t
          });

        var tOld = t - forMs;
        while( true ){
          if( storeData[key][0]['t'] < tOld ){
            //console.log("storeIt key:"+key+" drop to old!");
            storeData[key].shift();

          }else{
            //console.log("storeIt key:"+key+" is up to date..."+len);
            break;
          }

        }


    }
    function storeGetLast( key ){
      return storeData[key][ storeData[key].length-1 ];
    }
    function storeGetPreLast( key ){
      return storeData[key][ storeData[key].length-2 ];
    }

    function avgIt( key, forMs, toMs ){
  var now = new Date().getTime();

  var t = now-forMs;

  var tFrom = now;
  if( toMs != undefined )
    tFrom = t+toMs;

  var d = storeData[key];
  var l = d.length-1;
  var tr = 0;
  var trC = 0;


  for( var i=l; i>=0; i-- ){
    if( t <= d[i]['t'] && d[i]['t'] <= tFrom ){
      tr+= d[i]['v'];
      trC++;
    }

  }

  //console.log("avgIt tr"+tr+" trC"+trC+" res"+(tr/trC));
  return tr/trC;

}


