          var d = pidRes - this.tPos;


          if( Math.abs(d) > 0.25 ){
            this.tPos = pidRes;
            return Math.round(d*this.resp);
          }else
            return 0;
      }

      rudder_command = 0;
      pid2( delta ){
          // differential_error - delta
        // heading_error = heading_to_steer - heading; 
        // differential_error = bearingrate; // 0 - 0.5
        var PID_output = this.pid.k_p * (this.pid.k_i * delta  - this.pid.k_d * this.gain2); 
        return this.rudder_command + PID_output;
      }

      update( degMag ){
          storeIt('ap_degMag', degMag, sec30);
          this.hdm = avgIt('ap_degMag',sec1*2);
          this.delta = degDelta( this.hdm, this.target );
          this.pidRes = this.pid.update( this.delta-(this.tPos*this.gain2) );
          this.rudder_command = this.pidRes;
          storeIt('pidRes', this.pidRes, sec30);

          //this.pidRes = this.pid.update( this.delta - this.pidRes );



          return {
            hdm: this.hdm,
            target: this.target,
            delta: this.delta,
            pidRes: this.pidRes,
            pidResAvgSec5: avgIt('pidRes',sec1*5),
            tillerPos: this.tPos,
            tillerBy: this.calTillerMove( this.pidRes ),
            pidAng: this.ang,
            time: new Date()
            };
      }

      setTarget( deg ){
