                    topic: 'NR/ap/tillerBy',
                    payload: t > 0 ? 200 : -200
                }
            ]);
        }
    }else if( msg.payload.substring(0,19) == 'ap:correctTargetMs:' ){
        var t = parseFloat( msg.payload.substring(19) );
        node.send([
            null,
            {
                topic: 'NR/ap/tillerBy',
                payload: t 
            }
        ]);


    }else if( msg.payload.substring(0,6) == 'apDeb:' ){
        j = JSON.parse( msg.payload.substring(6) );
            node.send([
                {
                    topic: 'ap/debug',
                    payload: j
                },  
                null
            ]);




    }else if( msg.payload.substring(0,14) == 'ap:setSetting:' ){
        var set = msg.payload.substring(14).split(',');
        var conf = ap.getSettings();
        var i = 0;
        for(var k in conf){
            conf[k] = set[i++];
        }
        ap.setSettings( conf );
        sendSettingsStatus();
    }

}

// getting mqtt mag
function onNewMag( h ){
    if( ap.on ){
        res = ap.update( parseFloat(h) );
        var moveBy = Math.round( res.tillerBy*ap.config['tillerGain'] );
        if( Math.abs( moveBy ) < apMinMove && moveBy != 0 )
            moveBy = apMinMove * ( moveBy >= 0 ? 1 : -1 );

        if( moveBy != 0 )
            node.send([
                null,
                {
                    topic: 'NR/ap/tillerBy',
                    payload: moveBy.toString()
                }
                ]);

        res['mode'] = apMode;

        node.send([
            {
                topic: 'ap/workStat',
                payload: res
            },
            null
            ]);
    }

    node.status({
        text:"mag:"+h+" ap.on:"+(ap.on?'1':'0')

    });
}
