func:
if( typeof msg.payload == 'string' &&
    msg.payload.substring(0,10) == 'musicSync:' ){
    var j = msg.payload.substring(10).split(",");

    for( var c=0,cc=j.length; c<cc; c++ ){

        if( j[c] == 'play=1' ){
            node.send({
                topic:"musicSync/all",
                payload: 'play'
            });
        }else if( j[c].substring(0,5) == 'seek=' ){
            node.send({
                topic:"musicSync/allSeek",
                payload: j[c].substring(5)
            });
        }else{
            node.send({
                topic:"musicSync/cmd",
                payload: j[c].split("=")
            });
        }
    }

}
